mapper.select:
select 
    from users as user
    left join user_info as info on user.id = info.user_id
    left join balances as balance on user.id = balance.user_id

    user.is_deleted=0 and info.is_deleted=0 and balance.is_deleted=0
    and user.role = #{dto.role}
    and user.status = #{dto.status}
    and balance.balance >= #{dto.balanceFrom}
    and balance.balance <= #{dto.balanceTo}
    and balance.free_balance >= #{dto.freeBalanceFrom}
    and balance.free_balance <= #{dto.freeBalanceTo}
    and user.create_time >= #{dto.createTimeFrom}
    and user.create_time <= #{dto.createTimeTo}

select from users as user left join user_info as info on user.id = info.user_id left join balances as balance on user.id = balance.user_id
user.is_deleted=0 and info.is_deleted=0 and balance.is_deleted=0 and user.id = #{id}
limit 1

select from users as user left join user_info as info on user.id = info.user_id left join balances as balance on user.id = balance.user_id
user.is_deleted=0 and info.is_deleted=0 and balance.is_deleted=0 and user.id in
#{id}
